/*산술연산자(+,-,*,/,%) */
package step03;

public class Test01_1 {

  public static void main(String[] args) {
    // TODO Auto-generated method stub
    System.out.println(10 + 3);
    System.out.println(10 - 3);
    System.out.println(10 * 3);
    System.out.println(10 / 3); //결과: 3, 이유? int/int =int
    System.out.println(10 % 3);
  }

}


